import { NgModule } from "@angular/core";
import { NativeScriptRouterModule } from "nativescript-angular/router";
import { Routes } from "@angular/router";

import { AuthGuard } from "@src/app/shared/router/auth.guard";
// import { AutoGeneratedComponent } from "@src/app/auto-generated/auto-generated.component";
import { LayoutComponent } from "@src/app/layout/layout.component";
import { ConnectComponent } from "@src/app/connect/connect.component";


export const routes: Routes = [
	// {
	// 	path: "",
	// 	redirectTo: "/players",
	// 	pathMatch: "full",
	// },
	{
		path: "library",
		component: LayoutComponent,
		canLoad: [AuthGuard],
	// tslint:disable-next-line:newline-per-chained-call
		loadChildren: () => import("@src/app/content/content.module").then((m) => m.ContentModule),
	},
	{
		path: "connect",
		component: LayoutComponent,
		children: [
			{ path: "", component: ConnectComponent },
		],
	},
	{
		path: "",
		pathMatch: "full",
		redirectTo: "library",
	},
	// {
	// 	path: "auto-generated",
	// 	component: AutoGeneratedComponent,
	// },
];

@NgModule({
	imports: [NativeScriptRouterModule.forRoot(routes)],
	exports: [NativeScriptRouterModule]
})
export class AppRoutingModule { }
